import React, { useState } from "react";
import "../../homepage/style/homeStyle.scss";
import GroupData from "./GroupData";

const GroupForm = ({ members }) => {
    const [paidBy, setPaidBy] = useState("");
    const [splitOption, setSplitOption] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        // Add your form submission logic here
    };

    return (
        <>
            <div className="card-container">
                <form
                    onSubmit={handleSubmit}
                    className="settled-card"
                >
                    <div>
                        <label>
                            Description:
                            <input type="text" />
                        </label>
                    </div>

                    <div>
                        <label>
                            Total Pay Amount:
                            <input type="number" />
                        </label>
                    </div>

                    <div>
                        <label className="label">
                            Paid By:
                            <select
                                value={paidBy}
                                onChange={(e) => setPaidBy(e.target.value)}
                            >
                                <option value="">Select</option>
                                {members.map((member, index) => (
                                    <option
                                        key={index}
                                        value={member.name}
                                    >
                                        {member.name}
                                    </option>
                                ))}
                            </select>
                            and split
                            <select
                                value={splitOption}
                                onChange={(e) => setSplitOption(e.target.value)}
                            >
                                <option value="">Select</option>
                                <option value="equal">Equally</option>
                                <option value="percentage">Percentage</option>
                            </select>
                        </label>
                    </div>

                    <button
                        type="submit"
                        className="button"
                    >
                        Settled
                    </button>
                </form>
            </div>
        </>
    );
};

const ShowData = () => {
    const navigate = useNavigate();
    const data = {
        groupName: "subway",
        Description: "Group expenses",
        paidByName: "you",
        amount: "1000",
        members: [
            { name: "You", amount: "200.00" },
            { name: "Om", amount: "200.00" },
            { name: "Sapna", amount: "200.00" },
            { name: "Shruti", amount: "200.00" },
            { name: "Shivani", amount: "200.00" },
        ],
    };

    return (
        <div className="button-wrapper">
            <GroupData {...data} />
            <button
                className="expense-button"
                onClick={() => {
                    navigate("/AddExpense");
                }}
            >
                Add Expense
            </button>
        </div>
    );
};

export default ShowData;
