import React, { useState } from "react";

const ExpenseTracker = () => {
    const [groupMembers, setGroupMembers] = useState([]);
    const [expenses, setExpenses] = useState({});
    const [newMember, setNewMember] = useState("");

    const addGroupMember = () => {
        if (groupMembers.length >= 5) {
            alert("Maximum group size reached.");
            return;
        }

        if (groupMembers.includes(newMember)) {
            alert("Member already exists in the group.");
            return;
        }

        setGroupMembers([...groupMembers, newMember]);
        setExpenses({ ...expenses, [newMember]: 0 });
        setNewMember("");
    };

    const removeGroupMember = (member) => {
        const updatedMembers = groupMembers.filter((m) => m !== member);
        const updatedExpenses = { ...expenses };
        delete updatedExpenses[member];

        setGroupMembers(updatedMembers);
        setExpenses(updatedExpenses);
    };

    const addExpense = (member, amount) => {
        setExpenses({ ...expenses, [member]: expenses[member] + amount });
    };

    const splitExpenses = () => {
        const totalMembers = groupMembers.length;
        const totalExpenses = Object.values(expenses).reduce(
            (acc, cur) => acc + cur,
            0
        );
        const splitAmount = totalExpenses / totalMembers;

        const updatedExpenses = {};
        groupMembers.forEach((member) => {
            updatedExpenses[member] = splitAmount;
        });

        setExpenses(updatedExpenses);
    };

    return (
        <div>
            <h2>Expense Tracker</h2>

            <div>
                <h3>Add Group Members</h3>
                <input
                    type="text"
                    value={newMember}
                    onChange={(e) => setNewMember(e.target.value)}
                />
                <button onClick={addGroupMember}>Add Member</button>
            </div>

            <div>
                <h3>Group Members</h3>
                <ul>
                    {groupMembers.map((member) => (
                        <li key={member}>
                            {member}
                            <button onClick={() => removeGroupMember(member)}>
                                Remove
                            </button>
                        </li>
                    ))}
                </ul>
            </div>

            <div>
                <h3>Add Expense</h3>
                <select
                    value={selectedMember}
                    onChange={(e) => setSelectedMember(e.target.value)}
                >
                    {groupMembers.map((member) => (
                        <option
                            key={member}
                            value={member}
                        >
                            {member}
                        </option>
                    ))}
                </select>
                <input
                    type="number"
                    value={expenseAmount}
                    onChange={(e) => setExpenseAmount(Number(e.target.value))}
                />
                <button
                    onClick={() => addExpense(selectedMember, expenseAmount)}
                >
                    Add Expense
                </button>
            </div>

            <div>
                <h3>Expenses and Splits</h3>
                {Object.entries(expenses).map(([member, amount]) => (
                    <p key={member}>
                        {member}: {amount}
                    </p>
                ))}
                <button onClick={splitExpenses}>Split Equally</button>
            </div>
        </div>
    );
};

export default ExpenseTracker;
