import React, { useState } from "react";
import "../../homepage/style/homeStyle.scss";

const GroupForm = ({ members, onUpdateData }) => {
    const [description, setDescription] = useState("");
    const [totalPayAmount, setTotalPayAmount] = useState("");
    const [paidBy, setPaidBy] = useState("");
    const [splitOption, setSplitOption] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        // Perform validation or data processing logic here

        // Prepare the updated data
        const updatedData = {
            description: description,
            totalPayAmount: totalPayAmount,
            paidBy: paidBy,
            splitOption: splitOption,
        };

        // Call the callback function to update the data in the parent component
        onUpdateData(updatedData);
    };

    return (
        <div className="card-container">
            <form
                onSubmit={handleSubmit}
                className="card"
            >
                <div>
                    <label>
                        Description:
                        <input
                            type="text"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </label>
                </div>

                <div>
                    <label>
                        Total Pay Amount:
                        <input
                            type="number"
                            value={totalPayAmount}
                            onChange={(e) => setTotalPayAmount(e.target.value)}
                        />
                    </label>
                </div>

                <div>
                    <label className="label">
                        Paid By:
                        <select
                            value={paidBy}
                            onChange={(e) => setPaidBy(e.target.value)}
                        >
                            <option value="">Select</option>
                            {members.map((member, index) => (
                                <option
                                    key={index}
                                    value={member.name}
                                >
                                    {member.name}
                                </option>
                            ))}
                        </select>
                        and split
                        <select
                            value={splitOption}
                            onChange={(e) => setSplitOption(e.target.value)}
                        >
                            <option value="">Select</option>
                            <option value="equal">Equally</option>
                            <option value="percentage">Percentage</option>
                        </select>
                    </label>
                </div>

                <button
                    type="submit"
                    className="button"
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default GroupForm;
