import React, { useState } from "react";

// Parent component
const ExpenseList = ({ expenseData }) => {
    const [splitAmount, setSplitAmount] = useState(0);

    // Calculate the split amount
    const calculateSplitAmount = () => {
        const totalExpense = expenseData.reduce(
            (sum, expense) => sum + expense.amount,
            0
        );
        const numberOfPeople = 5;
        const amountPerPerson = totalExpense / numberOfPeople;
        setSplitAmount(amountPerPerson);
    };

    return (
        <div>
            <h2>Expense List</h2>
            <button onClick={calculateSplitAmount}>Split Bill</button>
            <div>
                {expenseData.map((expense) => (
                    <ExpenseItem
                        key={expense.id}
                        expense={expense}
                    />
                ))}
            </div>
            <SplitAmount splitAmount={splitAmount} />
        </div>
    );
};

// Child component for displaying individual expenses
const ExpenseItem = ({ expense }) => {
    return (
        <div>
            <p>Item: {expense.name}</p>
            <p>Cost: {expense.amount}</p>
        </div>
    );
};

// Child component for displaying the split amount per person
const SplitAmount = ({ splitAmount }) => {
    return (
        <div>
            <h3>Split Amount</h3>
            <p>{splitAmount} per person</p>
        </div>
    );
};

// Usage
const Group = () => {
    const expenseData = [
        { id: 1, name: "Dinner", amount: 50 },
        { id: 2, name: "Movie tickets", amount: 30 },
        { id: 3, name: "Groceries", amount: 100 },
        // ... other expenses
    ];

    return <ExpenseList expenseData={expenseData} />;
};

export default Group;
