import React, { useState, useEffect } from "react";

const GroupExpenseSplitter = () => {
    const [members, setMembers] = useState([]);
    const [expense, setExpense] = useState("");
    const [totalExpense, setTotalExpense] = useState(0);

    useEffect(() => {
        const storedMembers = localStorage.getItem("groupMembers");
        if (storedMembers) {
            setMembers(JSON.parse(storedMembers));
        }
    }, []);

    useEffect(() => {
        localStorage.setItem("groupMembers", JSON.stringify(members));
    }, [members]);

    const handleAddMember = () => {
        const name = prompt("Enter member name:");
        if (name) {
            setMembers([...members, { name, expense: 0 }]);
        }
    };

    const handleRemoveMember = (index) => {
        const updatedMembers = [...members];
        updatedMembers.splice(index, 1);
        setMembers(updatedMembers);
    };

    const handleExpenseChange = (event) => {
        setExpense(event.target.value);
    };

    const handleExpenseSplit = () => {
        const updatedMembers = members.map((member) => ({
            ...member,
            expense:
                parseFloat(member.expense) +
                parseFloat(expense) / members.length,
        }));
        setMembers(updatedMembers);
        setTotalExpense(totalExpense + parseFloat(expense));
        setExpense("");
    };

    return (
        <div>
            <h2>Group Expense Splitter</h2>

            <button onClick={handleAddMember}>Add Member</button>

            <ul>
                {members.map((member, index) => (
                    <li key={index}>
                        {member.name} ({member.expense.toFixed(2)})
                        <button onClick={() => handleRemoveMember(index)}>
                            Remove
                        </button>
                    </li>
                ))}
            </ul>

            <div>
                <input
                    type="number"
                    value={expense}
                    onChange={handleExpenseChange}
                    placeholder="Enter expense amount"
                />
                <button onClick={handleExpenseSplit}>Split Expense</button>
            </div>

            <p>Total Expense: {totalExpense.toFixed(2)}</p>
        </div>
    );
};

export default GroupExpenseSplitter;
