import React, { useState, useEffect } from "react";

const ExpenseTracker = () => {
    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [paidBy, setPaidBy] = useState("");
    const [groupMembers, setGroupMembers] = useState([]);
    const [expenses, setExpenses] = useState([]);

    useEffect(() => {
        const storedGroupMembers = localStorage.getItem("groupMembers");
        if (storedGroupMembers) {
            setGroupMembers(JSON.parse(storedGroupMembers));
        }

        const storedExpenses = localStorage.getItem("expenses");
        if (storedExpenses) {
            setExpenses(JSON.parse(storedExpenses));
        }
    }, []);

    useEffect(() => {
        localStorage.setItem("groupMembers", JSON.stringify(groupMembers));
    }, [groupMembers]);

    useEffect(() => {
        localStorage.setItem("expenses", JSON.stringify(expenses));
    }, [expenses]);

    const handleAddMember = () => {
        if (groupMembers.includes(paidBy)) {
            alert("Member already exists!");
            return;
        }
        setGroupMembers([...groupMembers, paidBy]);
    };

    const handleRemoveMember = (member) => {
        const updatedMembers = groupMembers.filter((m) => m !== member);
        setGroupMembers(updatedMembers);
    };

    const handleExpenseSubmit = () => {
        const expense = { description, amount, paidBy };
        setExpenses([...expenses, expense]);
        setDescription("");
        setAmount("");
        setPaidBy("");
    };

    const handleSplitExpenses = () => {
        const numMembers = groupMembers.length;
        if (numMembers === 0) {
            alert("No group members found!");
            return;
        }

        const perPersonAmount = parseFloat(amount) / numMembers;

        const splitExpenses = groupMembers.map((member) => ({
            description,
            amount: perPersonAmount.toFixed(2),
            paidBy,
            splitWith: member,
        }));

        setExpenses([...expenses, ...splitExpenses]);
        setDescription("");
        setAmount("");
        setPaidBy("");
    };

    return (
        <div>
            <h1>Expense Tracker</h1>
            <div>
                <label>
                    Description:
                    <input
                        type="text"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                </label>
            </div>
            <div>
                <label>
                    Amount:
                    <input
                        type="number"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                    />
                </label>
            </div>
            <div>
                <label>
                    Paid By:
                    <input
                        type="text"
                        value={paidBy}
                        onChange={(e) => setPaidBy(e.target.value)}
                    />
                    <button onClick={handleAddMember}>Add Member</button>
                </label>
            </div>
            <div>
                <h2>Group Members:</h2>
                <ul>
                    {groupMembers.map((member) => (
                        <li key={member}>
                            {member}
                            <button onClick={() => handleRemoveMember(member)}>
                                Remove
                            </button>
                        </li>
                    ))}
                </ul>
            </div>
            <div>
                <button onClick={handleExpenseSubmit}>Add Expense</button>
                <button onClick={handleSplitExpenses}>Split Expenses</button>
            </div>
            <div>
                <h2>Expenses:</h2>
                <ul>
                    {expenses.map((expense, index) => (
                        <li key={index}>
                            <p>Description: {expense.description}</p>
                            <p>Amount: {expense.amount}</p>
                            <p>Paid By: {expense.paidBy}</p>
                            <p>Split With: {expense.splitWith || "N/A"}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ExpenseTracker;
