import React, { useState, useEffect } from "react";

const GroupForm = ({ onSave }) => {
    const [groupName, setGroupName] = useState("");
    const [paidByName, setPaidByName] = useState("");
    const [amount, setAmount] = useState("");
    const [members, setMembers] = useState([]);

    const handleAddMember = () => {
        setMembers([...members, { name: "", amount: 0 }]);
    };

    const handleMemberChange = (index, field, value) => {
        const updatedMembers = [...members];
        updatedMembers[index][field] = value;
        setMembers(updatedMembers);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const groupData = {
            groupName,
            paidByName,
            amount,
            members,
        };

        onSave(groupData);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Group Name:
                <input
                    type="text"
                    value={groupName}
                    onChange={(event) => setGroupName(event.target.value)}
                />
            </label>

            <label>
                Paid By:
                <input
                    type="text"
                    value={paidByName}
                    onChange={(event) => setPaidByName(event.target.value)}
                />
            </label>

            <label>
                Total Pay Amount:
                <input
                    type="number"
                    value={amount}
                    onChange={(event) => setAmount(event.target.value)}
                />
            </label>

            <h3>Members:</h3>
            {members.map((member, index) => (
                <div key={index}>
                    <label>
                        Name:
                        <input
                            type="text"
                            value={member.name}
                            onChange={(event) =>
                                handleMemberChange(
                                    index,
                                    "name",
                                    event.target.value
                                )
                            }
                        />
                    </label>

                    <label>
                        Splitable Amount:
                        <input
                            type="number"
                            value={member.amount}
                            onChange={(event) =>
                                handleMemberChange(
                                    index,
                                    "amount",
                                    event.target.value
                                )
                            }
                        />
                    </label>
                </div>
            ))}
            <button
                type="button"
                onClick={handleAddMember}
            >
                Add Member
            </button>

            <button type="submit">Save Group</button>
        </form>
    );
};

const GroupList = ({ groups, onGroupSelect }) => {
    return (
        <ul>
            {groups.map((group, index) => (
                <li
                    key={index}
                    onClick={() => onGroupSelect(group)}
                >
                    {group.groupName}
                </li>
            ))}
        </ul>
    );
};

const GroupDetails = ({ group }) => {
    return (
        <div>
            <h2>{group.groupName}</h2>
            <p>Paid By: {group.paidByName}</p>
            <p>Total Pay Amount: {group.amount}</p>
            <h3>Members:</h3>
            <ul>
                {group.members.map((member, index) => (
                    <li key={index}>
                        Name: {member.name}, Splitable Amount: {member.amount}
                    </li>
                ))}
            </ul>
        </div>
    );
};

const App = () => {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState(null);

    useEffect(() => {
        const savedGroups = loadFromLocalStorage();
        if (savedGroups) {
            setGroups(savedGroups);
        }
    }, []);

    const handleSaveGroup = (groupData) => {
        const updatedGroups = [...groups, groupData];
        setGroups(updatedGroups);
        saveToLocalStorage(updatedGroups);
    };

    const saveToLocalStorage = (data) => {
        localStorage.setItem("groups", JSON.stringify(data));
    };

    const loadFromLocalStorage = () => {
        const data = localStorage.getItem("groups");
        return data ? JSON.parse(data) : null;
    };

    const handleGroupSelect = (group) => {
        setSelectedGroup(group);
    };

    return (
        <div>
            <GroupForm onSave={handleSaveGroup} />
            <GroupList
                groups={groups}
                onGroupSelect={handleGroupSelect}
            />
            {selectedGroup && <GroupDetails group={selectedGroup} />}
        </div>
    );
};
