import React, { useState, useEffect } from 'react';

const App = () => {
  const [friends, setFriends] = useState([]);
  const [groups, setGroups] = useState([]);
  const [newFriend, setNewFriend] = useState('');
  const [groupName, setGroupName] = useState('');
  const [groupMembers, setGroupMembers] = useState([]);
  const [expenseDescription, setExpenseDescription] = useState('');
  const [expenseAmount, setExpenseAmount] = useState('');
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    const storedFriends = localStorage.getItem('friends');
    const storedGroups = localStorage.getItem('groups');
    const storedExpenses = localStorage.getItem('expenses');

    if (storedFriends) {
      setFriends(JSON.parse(storedFriends));
    }
    if (storedGroups) {
      setGroups(JSON.parse(storedGroups));
    }
    if (storedExpenses) {
      setExpenses(JSON.parse(storedExpenses));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('friends', JSON.stringify(friends));
  }, [friends]);

  useEffect(() => {
    localStorage.setItem('groups', JSON.stringify(groups));
  }, [groups]);

  useEffect(() => {
    localStorage.setItem('expenses', JSON.stringify(expenses));
  }, [expenses]);

  const handleAddFriend = () => {
    if (newFriend.trim() !== '' && !friends.includes(newFriend)) {
      setFriends([...friends, newFriend]);
      setNewFriend('');
    }
  };

  const handleRemoveFriend = (friend) => {
    setFriends(friends.filter((f) => f !== friend));
  };

  const handleAddGroup = () => {
    if (groupName.trim() !== '') {
      const group = {
        name: groupName,
        members: groupMembers,
      };
      setGroups([...groups, group]);
      setGroupName('');
      setGroupMembers([]);
    }
  };

  const handleAddExpense = () => {
    if (expenseDescription.trim() !== '' && expenseAmount > 0) {
      const expense = {
        description: expenseDescription,
        amount: expenseAmount,
      };
      setExpenses([...expenses, expense]);
      setExpenseDescription('');
      setExpenseAmount('');
    }
  };

  return (
    <div>
      <h1>Splitwise App</h1>

      <h2>Friends</h2>
      <ul>
        {friends.map((friend) => (
          <li key={friend}>
            {friend}
            <button type="button" onClick={() => handleRemoveFriend(friend)}>
              Remove
            </button>
          </li>
        ))}
      </ul>

      <div>
        <input
          type="text"
          value={newFriend}
          onChange={(e) => setNewFriend(e.target.value)}
        />
        <button type="button" onClick={handleAddFriend}>
          Add Friend
        </button>
      </div>

      <h2>Groups</h2>
      <ul>
        {groups.map((group, index) => (
          <li key={index}>
            {group.name}:
            <ul>
              {group.members.map((member) => (
                <li key={member}>{member}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>

      <div>
        <input
          type="text"
          value={groupName}
          onChange={(e) => setGroupName(e.target.value)}
        />
        <ul>
          {groupMembers.map((member, index) => (
            <li key={index}>{member}</li>
          ))}
        </ul>
        <input
          type="text"
          value={newMember}
          onChange={(e) => setNewMember(e.target.value)}
        />
        <button type="button" onClick={() => setGroupMembers([...groupMembers, newMember])}>
          Add Member
        </button>
        <button type="button" onClick={handleAddGroup}>
          Create Group
        </button>
      </div>

      <h2>Expenses</h2>
      <ul>
        {expenses.map((expense, index) => (
          <li key={index}>
            {expense.description}: {expense.amount}
          </li>
        ))}
      </ul>

      <div>
        <input
          type="text"
          value={expenseDescription}
          onChange={(e) => setExpenseDescription(e.target.value)}
        />
        <input
          type="number"
          value={expenseAmount}
          onChange={(e) => setExpenseAmount(e.target.value)}
        />
        <button type="button" onClick={handleAddExpense}>
          Add Expense
        </button>
      </div>
    </div>
  );
};

export default App;
