import React, { useState } from 'react';

const ExpenseForm = () => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState(0);
  const [members, setMembers] = useState(['Member 1', 'Member 2', 'Member 3']);
  const [paidBy, setPaidBy] = useState('');

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleAmountChange = (e) => {
    setAmount(parseFloat(e.target.value));
  };

  const handlePaidByChange = (e) => {
    setPaidBy(e.target.value);
  };

  const handleMemberChange = (member, e) => {
    const updatedMembers = e.target.checked
      ? [...members, member]
      : members.filter((m) => m !== member);

    setMembers(updatedMembers);
  };

  const handleExpenseSubmit = (e) => {
    e.preventDefault();

    // Store the expense data in local storage
    const expense = {
      description,
      amount,
      members,
      paidBy,
    };
    localStorage.setItem('expense', JSON.stringify(expense));

    // Reset form fields
    setDescription('');
    setAmount(0);
    setPaidBy('');
  };

  return (
    <form onSubmit={handleExpenseSubmit}>
      <div>
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={handleDescriptionChange}
        />
      </div>
      <div>
        <label>Amount:</label>
        <input
          type="number"
          value={amount}
          onChange={handleAmountChange}
        />
      </div>
      <div>
        <label>Paid By:</label>
        <select value={paidBy} onChange={handlePaidByChange}>
          {members.map((member) => (
            <option key={member} value={member}>
              {member}
            </option>
          ))}
        </select>
      </div>
      <div>
        <p>Members:</p>
        {members.map((member) => (
          <div key={member}>
            <input
              type="checkbox"
              value={member}
              onChange={(e) => handleMemberChange(member, e)}
              checked={members.includes(member)}
            />
            <label>{member}</label>
          </div>
        ))}
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default ExpenseForm;
