import React, { useState } from 'react';

const AddExpense = ({ friends, handleAddExpense }) => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [selectedFriends, setSelectedFriends] = useState([]);

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleAmountChange = (e) => {
    setAmount(e.target.value);
  };

  const handleFriendToggle = (friendId) => {
    if (selectedFriends.includes(friendId)) {
      setSelectedFriends(selectedFriends.filter((id) => id !== friendId));
    } else {
      setSelectedFriends([...selectedFriends, friendId]);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const expense = {
      description,
      amount,
      friends: selectedFriends,
    };
    handleAddExpense(expense);
    setDescription('');
    setAmount('');
    setSelectedFriends([]);
  };

  return (
    <div>
      <h2>Add Expense</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Description:</label>
          <input
            type="text"
            value={description}
            onChange={handleDescriptionChange}
          />
        </div>
        <div>
          <label>Amount:</label>
          <input
            type="number"
            value={amount}
            onChange={handleAmountChange}
          />
        </div>
        <div>
          <label>Select Friends:</label>
          {friends.map((friend) => (
            <div key={friend.id}>
              <input
                type="checkbox"
                checked={selectedFriends.includes(friend.id)}
                onChange={() => handleFriendToggle(friend.id)}
              />
              <span>{friend.name}</span>
            </div>
          ))}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const GroupManager = () => {
  const [groupName, setGroupName] = useState('');
  const [groupMembers, setGroupMembers] = useState([]);
  const [newMember, setNewMember] = useState('');

  const handleGroupNameChange = (e) => {
    setGroupName(e.target.value);
  };

  const handleNewMemberChange = (e) => {
    setNewMember(e.target.value);
  };

  const handleAddMember = () => {
    if (newMember !== '' && !groupMembers.includes(newMember)) {
      setGroupMembers([...groupMembers, newMember]);
      setNewMember('');
    }
  };

  const handleRemoveMember = (member) => {
    setGroupMembers(groupMembers.filter((m) => m !== member));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle group submission logic here
    console.log('Group submitted:', groupName, groupMembers);
    setGroupName('');
    setGroupMembers([]);
  };

  return (
    <div>
      <h2>Create Group</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Group Name:</label>
          <input
            type="text"
            value={groupName}
            onChange={handleGroupNameChange}
          />
        </div>
        <div>
          <label>Group Members:</label>
          <ul>
            {groupMembers.map((member) => (
              <li key={member}>
                {member}
                <button type="button" onClick={() => handleRemoveMember(member)}>
                  Remove
                </button>
              </li>
            ))}
          </ul>
          <div>
            <input
              type="text"
              value={newMember}
              onChange={handleNewMemberChange}
            />
            <button type="button" onClick={handleAddMember}>
              Add Member
            </button>
          </div>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const App = () => {
  const [expenses, setExpenses] = useState([]);
  const [groups, setGroups] = useState([]);

  const handleAddExpense = (expense) => {
    setExpenses([...expenses, expense]);
  };

  const handleAddGroup = (group) => {
    setGroups([...groups, group]);
  };

  return (
    <div>
      <h1>Splitwise App</h1>
      <AddExpense friends={[]} handleAddExpense={handleAddExpense} />
      <GroupManager handleAddGroup={handleAddGroup} />
    </div>
  );
};

export default App;
