import React from "react";

const TripBuddyGroup = () => {
    const groupName = "Trip Buddy Group";
    const members = [
        { name: "Alice", totalExpenses: 150 },
        { name: "Bob", totalExpenses: 200 },
        { name: "Claire", totalExpenses: 100 },
        { name: "David", totalExpenses: 250 },
        { name: "Emily", totalExpenses: 175 },
    ];

    const totalExpenses = members.reduce(
        (acc, member) => acc + member.totalExpenses,
        0
    );
    const numberOfMembers = members.length;
    const splittableAmount = totalExpenses / numberOfMembers;

    const updatedMembers = members.map((member) => {
        const payableAmount = member.totalExpenses - splittableAmount;
        return { ...member, payableAmount };
    });

    const totalPayByMembers = updatedMembers.reduce(
        (acc, member) => acc + member.totalExpenses,
        0
    );

    return (
        <div>
            <h2>{groupName}</h2>
            <h3>Paid By Members</h3>
            <ul>
                {updatedMembers.map(
                    (member, index) =>
                        member.payableAmount > 0 && (
                            <li key={index}>
                                <span>
                                    {member.name} (Paid: ₹{member.totalExpenses}
                                    )
                                </span>
                            </li>
                        )
                )}
            </ul>
            <h3>Total Pay By Members: ₹{totalPayByMembers}</h3>
            <h3>Total Pay Amount: ₹{totalExpenses}</h3>
            <h3>Splitable Data</h3>
            <ul>
                {updatedMembers.map((member, index) => (
                    <li key={index}>
                        <span>{member.name}</span>
                        <span>Total Expenses: ₹{member.totalExpenses}</span>
                        <span>Payable Amount: ₹{member.payableAmount}</span>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TripBuddyGroup;
