import React, { useState } from "react";
// import { useNavigate } from "react-router-dom";
import GroupData from "./Group";
import GroupForm from "../components/groupExpense";

const ShowData = () => {
    // const navigate = useNavigate();
    const data = {
        groupName: "subway",
        paidByName: "you",
        amount: "1000",
        members: [
            { name: "You", amount: "200.00" },
            { name: "Om", amount: "200.00" },
            { name: "Sapna", amount: "200.00" },
            { name: "Shruti", amount: "200.00" },
            { name: "Shivani", amount: "200.00" },
        ],
    };

    const [showForm, setShowForm] = useState(false);

    const handleAddExpenseClick = () => {
        setShowForm(true);
    };

    return (
        <div>
            <GroupData {...data} />
            {showForm ? (
                <GroupForm members={data.members} />
            ) : (
                <button onClick={handleAddExpenseClick}>Add Expense</button>
            )}
        </div>
    );
};

export default ShowData;
