import React, { useState, useEffect } from "react";
import "../../homepage/style/homeStyle.scss";

const GroupForm = ({ onSave }) => {
    const [groupName] = useState("");
    const [amount, setAmount] = useState(0);
    const [members, setMembers] = useState([
        "You",
        "Om",
        "Sapna",
        "Shruti",
        "Shivani",
    ]);

    const [paidBy, setPaidBy] = useState("");
    const [newMember, setNewMember] = useState("");
    const [showInput, setShowInput] = useState(false);

    const handlePaidByChange = (e) => {
        setPaidBy(e.target.value);
    };

    const handleAddMember = (e) => {
        e.preventDefault();

        if (newMember.trim() !== "") {
            setMembers([...members, newMember]);
            setNewMember("");
            setShowInput(false);
        }
    };

    const handleRemoveMember = (member) => {
        setMembers(members.filter((m) => m !== member));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const totalMembers = members.length;
        const splitAmount = parseFloat(amount / totalMembers).toFixed(2);

        const groupData = {
            groupName,
            paidByName: paidBy,
            amount,
            members: members.map((member) => ({
                name: member,
                amount: splitAmount,
            })),
        };

        onSave(groupData);
    };

    const handleAddMemberClick = () => {
        setShowInput(true);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Description:
                    <input
                        type="text"
                        value={groupDescript}
                    />
                </label>
            </div>

            <div className="member-container">
                <p>Split With:</p>
                {members.map((member) => (
                    <div key={member}>
                        <label className="member-label">{member}</label>
                        <button
                            type="button"
                            className="member-button"
                            onClick={() => handleRemoveMember(member)}
                        >
                            ╳
                        </button>
                    </div>
                ))}
            </div>

            <div>
                {showInput ? (
                    <>
                        <input
                            type="text"
                            value={newMember}
                            onChange={(event) =>
                                setNewMember(event.target.value)
                            }
                            placeholder="New member name"
                        />
                        <button
                            type="button"
                            className="button"
                            onClick={handleAddMember}
                            disabled={newMember.trim() === ""}
                        >
                            +
                        </button>
                    </>
                ) : (
                    <button
                        type="button"
                        className="button"
                        onClick={handleAddMemberClick}
                    >
                        +
                    </button>
                )}
            </div>

            <div>
                <label className="label">Paid By:</label>
                <select
                    value={paidBy}
                    className="select"
                    onChange={handlePaidByChange}
                >
                    {members.map((member) => (
                        <option
                            key={member}
                            value={member}
                        >
                            {member}
                        </option>
                    ))}
                </select>
            </div>

            <div>
                <label>
                    Total Pay Amount:
                    <input
                        type="number"
                        value={amount}
                        onChange={(event) => setAmount(event.target.value)}
                    />
                </label>
            </div>
            <button
                type="submit"
                className="button"
            >
                Add Group
            </button>
        </form>
    );
};

const GroupList = ({ groups, onGroupSelect, onDeleteGroup }) => {
    return (
        <ul>
            {groups.map((group, index) => (
                <li
                    key={index}
                    onClick={() => onGroupSelect(group)}
                >
                    {group.groupName}
                    <button onClick={() => onDeleteGroup(index)}>Delete</button>
                </li>
            ))}
        </ul>
    );
};

const GroupDetails = ({ group }) => {
    return (
        <div>
            <h2>{group.groupName}</h2>
            <p>Paid By: {group.paidByName}</p>
            <p>Total Pay Amount: {group.amount}</p>
            <h3>Members:</h3>
            <ul>
                {group.members.map((member, index) => (
                    <li key={index}>
                        {member.name} - {member.amount}
                    </li>
                ))}
            </ul>
        </div>
    );
};

const AppMain = () => {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState(null);

    useEffect(() => {
        const savedGroups = loadFromLocalStorage();
        if (savedGroups) {
            setGroups(savedGroups);
        }
    }, []);

    const handleSaveGroup = (groupData) => {
        const updatedGroups = [...groups, groupData];
        setGroups(updatedGroups);
        saveToLocalStorage(updatedGroups);
    };

    const handleGroupSelect = (group) => {
        setSelectedGroup(group);
    };

    const handleDeleteGroup = (index) => {
        const updatedGroups = [...groups];
        updatedGroups.splice(index, 1);
        setGroups(updatedGroups);
        saveToLocalStorage(updatedGroups);
    };

    const saveToLocalStorage = (data) => {
        localStorage.setItem("groups", JSON.stringify(data));
    };

    const loadFromLocalStorage = () => {
        const data = localStorage.getItem("groups");
        return data ? JSON.parse(data) : null;
    };

    return (
        <div>
            <GroupForm onSave={handleSaveGroup} />
            <GroupList
                groups={groups}
                onGroupSelect={handleGroupSelect}
                onDeleteGroup={handleDeleteGroup}
            />
            {selectedGroup && <GroupDetails group={selectedGroup} />}
        </div>
    );
};

export default AppMain;
