import React, { useState } from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import './App.css';
import Lending from '../src/feature/lendingPage/components/lendingPage.jsx';
import HomePage from '../src/feature/homepage/components/home.jsx';
import GroupForm from './feature/homepage/components/groupExpense';
import SplitForm from './feature/homepage/components/splitExpense';

function App() {
  const initialData = {
    groupName: "Flatmates",
    description: "Food party",
    paidByName: "Foram",
    amount: 1000,
    splitAmount: 200,
    members: [
      { name: "You", amount: "200", givenAmount: "00" },
      { name: "Om", amount: "00", givenAmount: "00" },
      { name: "Sapna", amount: "00", givenAmount: "00" },
      { name: "Shruti", amount: "00", givenAmount: "00" },
      { name: "Shivani", amount: "00", givenAmount: "00" },
    ],

  };

  //   {groupName: "subway", description: "sunday night ", paidByName: "You", amount: 1000,…}
  // amount
  // : 
  // 1000
  // description
  // : 
  // "sunday night "
  // groupName
  // : 
  // "subway"
  // members
  // : 
  // [{name: "You", amount: 200, givenAmount: 1000}, {name: "Om", amount: 200, givenAmount: 0},…]
  // paidByName
  // : 
  // "You"
  // splitAmount
  // : 
  // 200

  const [groupData, setGroupData] = useState(initialData);

  const handleGroupFormSubmit = (formData) => {
    setGroupData(formData);
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Lending />} />
        <Route path="/home" element={<HomePage groupData={groupData} />} />
        <Route
          path="/AddExpense"
          element={<GroupForm members={groupData.members} currentTotalAmount={groupData.amount} onSet={handleGroupFormSubmit} />}
        />
        <Route
          path="/SplitExpense"
          element={<SplitForm members={groupData.members} currentTotalAmount={groupData.amount} onSet={handleGroupFormSubmit} />}
        />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
