import React, { useState, useEffect } from 'react';

const GroupExpenseSplitter = () => {
  const [members, setMembers] = useState([]);
  const [expense, setExpense] = useState(0);

  // Load member data from local storage on component mount
  useEffect(() => {
    const storedMembers = localStorage.getItem('groupMembers');
    if (storedMembers) {
      setMembers(JSON.parse(storedMembers));
    }
  }, []);

  // Update local storage whenever members change
  useEffect(() => {
    localStorage.setItem('groupMembers', JSON.stringify(members));
  }, [members]);

  const addMember = () => {
    setMembers([...members, { name: '', id: Date.now() }]);
  };

  const removeMember = (id) => {
    setMembers(members.filter((member) => member.id !== id));
  };

  const updateMemberName = (id, newName) => {
    setMembers(
      members.map((member) => {
        if (member.id === id) {
          return { ...member, name: newName };
        }
        return member;
      })
    );
  };

  const splitExpense = () => {
    const numberOfMembers = members.length;
    const expensePerMember = expense / numberOfMembers;
    alert(`Expense per member: $${expensePerMember}`);
  };

  return (
    <div>
      <h2>Group Expense Splitter</h2>
      <button onClick={addMember}>Add Member</button>
      <ul>
        {members.map((member) => (
          <li key={member.id}>
            <input
              type="text"
              value={member.name}
              onChange={(e) => updateMemberName(member.id, e.target.value)}
            />
            <button onClick={() => removeMember(member.id)}>Remove</button>
          </li>
        ))}
      </ul>
      <div>
        <label>
          Total Expense: $
          <input
            type="number"
            value={expense}
            onChange={(e) => setExpense(parseFloat(e.target.value))}
          />
        </label>
      </div>
      <button onClick={splitExpense}>Split Expense</button>
    </div>
  );
};

export default GroupExpenseSplitter;
