import React, { useState, useEffect } from "react";
import "../../homepage/style/homeStyle.scss";

const GroupForm = ({ onSave }) => {
    const [groupName, setGroupName] = useState("");
    const [members, setMembers] = useState([
        "You",
        "Om",
        "Sapna",
        "Shruti",
        "Shivani",
    ]);

    const [newMember, setNewMember] = useState("");
    const [showInput, setShowInput] = useState(false);

    const handleAddMember = (e) => {
        e.preventDefault();

        if (newMember.trim() !== "") {
            setMembers([...members, newMember]);
            setNewMember("");
            setShowInput(false);
        }
    };

    const handleRemoveMember = (member) => {
        setMembers(members.filter((m) => m !== member));
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const groupData = {
            groupName,

            members: members.map((member) => ({
                name: member,
            })),
        };

        onSave(groupData);
    };

    const handleAddMemberClick = () => {
        setShowInput(true);
    };

    return (
        <form onSubmit={handleSubmit}>
            <div className="gro">
                <label>
                    Group Name:
                    <input
                        type="text"
                        value={groupName}
                        onChange={(event) => setGroupName(event.target.value)}
                    />
                </label>
            </div>

            <div className="member-container">
                <p>Members:</p>
                {members.map((member) => (
                    <div key={member}>
                        <label className="member-label">{member}</label>
                        <button
                            type="button"
                            className="member-button"
                            onClick={() => handleRemoveMember(member)}
                        >
                            â•³
                        </button>
                    </div>
                ))}
            </div>

            <div>
                {showInput ? (
                    <>
                        <input
                            type="text"
                            value={newMember}
                            onChange={(event) =>
                                setNewMember(event.target.value)
                            }
                            placeholder="New member name"
                        />
                        <button
                            type="button"
                            className="button"
                            onClick={handleAddMember}
                            disabled={newMember.trim() === ""}
                        >
                            Add Member
                        </button>
                    </>
                ) : (
                    <button
                        type="button"
                        className="button"
                        onClick={handleAddMemberClick}
                    >
                        Add Member
                    </button>
                )}
            </div>

            <button
                type="submit"
                className="button"
            >
                save
            </button>
        </form>
    );
};

const GroupList = ({ groups, onGroupSelect, onDeleteGroup }) => {
    return (
        <ul>
            {groups.map((group, index) => (
                <li
                    key={index}
                    onClick={() => onGroupSelect(group)}
                >
                    {group.groupName}
                    <button onClick={() => onDeleteGroup(index)}>Delete</button>
                </li>
            ))}
        </ul>
    );
};

const AddMembers = () => {
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        const savedGroups = loadFromLocalStorage();
        if (savedGroups) {
            setGroups(savedGroups);
        }
    }, []);

    const handleSaveGroup = (groupData) => {
        const updatedGroups = [...groups, groupData];
        setGroups(updatedGroups);
        saveToLocalStorage(updatedGroups);
    };

    const handleDeleteGroup = (index) => {
        const updatedGroups = [...groups];
        updatedGroups.splice(index, 1);
        setGroups(updatedGroups);
        saveToLocalStorage(updatedGroups);
    };

    const saveToLocalStorage = (data) => {
        localStorage.setItem("groups", JSON.stringify(data));
    };

    const loadFromLocalStorage = () => {
        const data = localStorage.getItem("groups");
        return data ? JSON.parse(data) : null;
    };

    return (
        <div>
            <GroupForm onSave={handleSaveGroup} />
            <GroupList
                groups={groups}
                onDeleteGroup={handleDeleteGroup}
            />
        </div>
    );
};

export default AddMembers;
