import React, { useState } from "react";

const SplitwiseApp = () => {
    const [expenses, setExpenses] = useState([]);
    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [paidBy, setPaidBy] = useState("user1");
    const [participants, setParticipants] = useState([]);

    const defaultParticipants = ["user1", "user2", "user3", "user4", "user5"];

    const handleAddExpense = () => {
        const newExpense = {
            description,
            amount,
            paidBy,
            participants,
        };

        setExpenses([...expenses, newExpense]);

        setDescription("");
        setAmount("");
        setPaidBy("user1");
        setParticipants([]);
    };

    return (
        <div>
            <h1>Splitwise Application</h1>

            <h2>Add Expense</h2>
            <label htmlFor="description">Expense Description:</label>
            <input
                type="text"
                id="description"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Description"
            />
            <br />

            <label htmlFor="amount">Expense Amount:</label>
            <input
                type="number"
                id="amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Amount"
            />
            <br />

            <label htmlFor="paid-by">Paid By:</label>
            <select
                id="paid-by"
                value={paidBy}
                onChange={(e) => setPaidBy(e.target.value)}
            >
                {defaultParticipants.map((participant) => (
                    <option
                        key={participant}
                        value={participant}
                    >
                        {participant}
                    </option>
                ))}
            </select>
            <br />

            <label>Participants:</label>
            {defaultParticipants.map((participant) => (
                <label key={participant}>
                    <input
                        type="checkbox"
                        checked={participants.includes(participant)}
                        onChange={(e) => {
                            if (e.target.checked) {
                                setParticipants([...participants, participant]);
                            } else {
                                setParticipants(
                                    participants.filter(
                                        (p) => p !== participant
                                    )
                                );
                            }
                        }}
                    />
                    {participant}
                </label>
            ))}
            <br />

            <button onClick={handleAddExpense}>Add Expense</button>

            <h2>Expense List</h2>
            {expenses.length === 0 ? (
                <p>No expenses added yet.</p>
            ) : (
                <ul>
                    {expenses.map((expense, index) => (
                        <li key={index}>
                            <strong>Description:</strong> {expense.description},{" "}
                            <strong>Amount:</strong> {expense.amount},{" "}
                            <strong>Paid By:</strong> {expense.paidBy},{" "}
                            <strong>Participants:</strong>{" "}
                            {expense.participants.join(", ")}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default SplitwiseApp;
