import React, { useState } from "react";
import "../../homepage/style/homeStyle.scss";
import User from "../../../images/user.png";
import AddMembers from "./AddGroups";

function UserId() {
    const [addExpense, setIsExpense] = useState(false);
    const [selectedGroup, setSelectedGroup] = useState(null);
    const data = JSON.parse(localStorage.getItem("groups")) || [];

    const handleAddExpense = () => {
        setIsExpense(true);
    };

    const handleDeleteGroup = (groupName) => {
        const updatedData = data.filter(
            (group) => group.groupName !== groupName
        );
        localStorage.setItem("groups", JSON.stringify(updatedData));
        setSelectedGroup(null);
    };

    return (
        <>
            <div className="user-section">
                <div className="user-profile">
                    <img
                        src={User}
                        alt="User logo"
                        className="user-image"
                    />
                    <div className="userInfo-wrapper">
                        <p className="user-name">Foram Patel</p>
                        <p className="user-email">forampatel188@gmail.com</p>
                    </div>
                </div>
                <div className="split-groups">
                    <button
                        onClick={handleAddExpense}
                        className="button"
                    >
                        Add Groups
                    </button>
                    {addExpense && <AddMembers />}
                </div>
            </div>
            <div className="group-list">
                {data.map((group, index) => (
                    <div
                        key={index}
                        className={`group-item ${
                            selectedGroup === group.groupName ? "selected" : ""
                        }`}
                        onClick={() => setSelectedGroup(group.groupName)}
                    >
                        <h3>{group.groupName}</h3>
                        <button
                            onClick={() => handleDeleteGroup(group.groupName)}
                        >
                            Delete
                        </button>
                        {selectedGroup === group.groupName && (
                            <div>{/* Display group data */}</div>
                        )}
                    </div>
                ))}
            </div>
        </>
    );
}

export default UserId;
