import React, { useState } from 'react';

const Group = () => {
  const [members, setMembers] = useState([
    { name: 'Member 1', money: 0 },
    { name: 'Member 2', money: 0 },
    { name: 'Member 3', money: 0 },
    { name: 'Member 4', money: 0 },
    { name: 'Member 5', money: 0 },
  ]);

  const handleSplitMoney = () => {
    const totalMembers = members.length;
    const moneyToSplit = 100; // Total money to be split among members

    const updatedMembers = members.map((member) => ({
      ...member,
      money: moneyToSplit / totalMembers,
    }));

    setMembers(updatedMembers);
  };

  return (
    <div>
      <h2>Group Members</h2>
      <ul>
        {members.map((member) => (
          <li key={member.name}>
            {member.name} - {member.money}
          </li>
        ))}
      </ul>
      <button onClick={handleSplitMoney}>Split Money</button>
    </div>
  );
};

export default Group;
