import React from "react";
import "../../homepage/style/homeStyle.scss";

const GroupData = ({ groupName, description, paidByName, amount, members }) => {
    const calculateTransaction = () => {
        const totalAmount = parseFloat(amount);
        const totalMembers = members?.length;
        const memberAmount = totalAmount / totalMembers;

        const transactions = members?.map((member) => ({
            giver: member.name,
            taker: paidByName,
            amount: memberAmount - parseFloat(member.givenAmount),
        }));

        return transactions.filter((transaction) => transaction.amount !== 0);
    };

    const transactions = calculateTransaction();

    return (
        <>
            <div className="group-information-wra">
                <h2>{groupName}</h2>
                <p>Description: {description}</p>
                <p>Paid by: {paidByName}</p>
                <p>Amount: {members[0].givenAmount}</p>

                <div className="transactions">
                    {transactions?.length > 0 && (
                        <div>
                            <h3>Transactions:</h3>
                            <ul>
                                {transactions.map(
                                    (transaction, index) =>
                                        transaction.giver !==
                                            transaction.taker && (
                                            <li key={index}>
                                                {transaction.giver} has to{" "}
                                                {transaction.amount > 0
                                                    ? "give"
                                                    : "take"}{" "}
                                                {Math.abs(transaction.amount)}{" "}
                                                to/from {transaction.taker}
                                            </li>
                                        )
                                )}
                            </ul>
                        </div>
                    )}
                </div>
                <h3>Members:</h3>
                <ul>
                    {members?.map((member, index) => (
                        <li key={index}>
                            {member.name} - {member.amount}
                        </li>
                    ))}
                </ul>
            </div>
        </>
    );
};

export default GroupData;
